# 基础导入与依赖
import requests
import re
import random
import time  # 新增导入
from datetime import datetime, timezone, timedelta  # 修改导入
from urllib.parse import quote, urlencode, unquote

# 基础配置（对齐Node.js headers）
headers = {
    'User-Agent': 'MiFit/6.12.0 (MCE16; Android 16; Density/1.5)',
    'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8',
    'app_name': 'com.xiaomi.hm.health',
    'Origin': 'https://user.zepp.com'
}
tz_bj = timezone(timedelta(hours=8))  # 北京时间时区

# 1. 提取code（内部依赖）
def get_code(location):
    code_pattern = re.compile(r"(?<=access=).*?(?=&)")
    match = code_pattern.findall(location)
    return match[0] if match else None

# 2. 登录函数（核心）
def login(account, password):
    try:
        # 账号类型判断+格式化
        is_phone = bool(re.match(r'^(1)\d{10}$', account))
        third_name = 'huami_phone' if is_phone else 'huami'
        formatted_account = unquote(account)
        if is_phone:
            formatted_account = '+86' + formatted_account.replace(r'^\+86|^86', '')

        # 第一步：获取access code
        encoded_account = quote(formatted_account)
        url1 = f"https://api-user.zepp.com/registrations/{encoded_account}/tokens"
        data1 = urlencode({
            'client_id': 'HuaMi', 'name': formatted_account, 'password': password,
            'redirect_uri': 'https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html',
            'token': 'access', 'country_code': 'CN', 'json_response': 'true', 'state': 'REDIRECTION'
        })
        resp1 = requests.post(url1, data=data1, headers=headers, allow_redirects=False, timeout=10)
        resp1.raise_for_status()

        # 提取code
        code = None
        if 'Location' in resp1.headers:
            code = get_code(resp1.headers['Location'])
        elif resp1.status_code == 200:
            resp1_json = resp1.json()
            if resp1_json.get('access'):
                code = resp1_json['access']
        if not code:
            raise Exception('获取code失败')

        # 第二步：获取login_token和user_id
        url2 = 'https://account.zepp.com/v2/client/login'
        data2 = urlencode({
            'allow_registration': 'false', 'app_name': 'com.xiaomi.hm.health', 'app_version': '6.12.0',
            'code': code, 'country_code': 'CN', 'device_id': 'fuck1069-2002-7869-0129-757geoi6sam1',
            'device_model': 'android_phone', 'dn': 'account.zepp.com,api-user.zepp.com,api-mifit.zepp.com,api-watch.zepp.com,app-analytics.zepp.com,api-analytics.huami.com,auth.zepp.com',
            'grant_type': 'access_token', 'source': 'com.xiaomi.hm.health', 'third_name': third_name
        })
        resp2 = requests.post(url2, data=data2, headers=headers, timeout=10)
        resp2.raise_for_status()
        resp2_json = resp2.json()

        token_info = resp2_json.get('token_info')
        if not token_info:
            raise Exception('未返回token_info')
        login_token = token_info.get('login_token')
        userid = token_info.get('user_id')
        if not login_token or not userid:
            raise Exception('token或userid缺失')

        return login_token, userid
    except Exception as e:
        print(f'登录失败: {str(e)}')
        return 0, 0

# 3. 获取app_token（核心）
def get_app_token(login_token):
    try:
        url = (
            f"https://account-cn.zepp.com/v1/client/app_tokens?"
            f"app_name=com.xiaomi.hm.health&"
            f"dn=api-user.zepp.com%2Capi-mifit.zepp.com%2Capp-analytics.zepp.com&"
            f"login_token={login_token}"
        )
        resp = requests.get(url, headers=headers, timeout=5)
        resp.raise_for_status()
        resp_json = resp.json()

        app_token = resp_json['token_info'].get('app_token')
        if not app_token:
            raise Exception('app_token缺失')
        return app_token
    except requests.exceptions.ConnectTimeout:
        print('请求超时，重试失败')
        return None
    except Exception as e:
        print(f'获取appToken失败: {str(e)}')
        return None

