def get_time():
    try:
        timestamp_url = "https://acs.m.taobao.com/gw/mtop.common.getTimestamp/"
        res = requests.get(timestamp_url, headers=BASE_HEADERS).json()
        return res["data"]["t"]
    except Exception as e:
        print(f"获取服务器时间戳失败，用本地时间替代：{str(e)}")
