def login(user, password):
    """登录小米运动账号，获取登录令牌和用户ID"""
    url1 = f"https://api-user.huami.com/registrations/{user}/tokens"
    headers_login = {
        "Content-Type": "application/x-www-form-urlencoded;charset=UTF-8",
        "User-Agent": "Mozilla/5.0 (iPhone; CPU iPhone OS 14_7_1 like Mac OS X) AppleWebKit/605.1.15"
    }
    data1 = {
        "client_id": "HuaMi",
        "password": password,
        "redirect_uri": "https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html",
        "token": "access"
    }
    r1 = requests.post(url1, data=data1, headers=headers_login, allow_redirects=False)
    location = r1.headers.get("Location", "")
    
    try:
        code = get_code(location)
    except:
        return 0, 0
    
    url2 = "https://account.huami.com/v2/client/login"
    data2 = {
        "allow_registration": "false",
        "app_name": "com.xiaomi.hm.health",
        "app_version": "6.3.5",
        "code": code,
        "country_code": "CN",
        "device_id": "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1",
        "device_model": "phone",
        "dn": "api-user.huami.com,api-mifit.huami.com,app-analytics.huami.com",
        "grant_type": "access_token",
        "lang": "zh_CN",
        "os_version": "1.5.0",
        "source": "com.xiaomi.hm.health",
        "third_name": "email",
    }
    r2 = requests.post(url2, data=data2, headers=headers).json()
    token_info = r2.get("token_info", {})
    return token_info.get("login_token"), token_info.get("user_id")

def get_app_token(login_token):
    """获取应用令牌"""
    if not login_token:
        return None
    url = f"https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com,api-mifit.huami.com,app-analytics.huami.com&login_token={login_token}"
    try:
        response = requests.get(url, headers=headers, timeout=5).json()
        return response.get("token_info", {}).get("app_token")
    except:
        print("获取app_token失败")
        return None

