def main(_user, _passwd, min_step=800, max_step=1000):
    """主函数：处理账号登录和步数提交"""
    user = str(_user).strip()
    password = str(_passwd).strip()
    step = random.randint(min_step, max_step)
    print(f"已设置随机步数({min_step}~{max_step}): {step}")
    
    if not user or not password:
        print("用户名或密码不能为空！")
        return "login fail!"
    
    login_token, userid = login(user, password)
    if not login_token or not userid:
        print("登录失败！请检查账号密码")
        return "login fail!"
    
    app_token = get_app_token(login_token)
    if not app_token:
        print("获取应用令牌失败！")
        return "token fail!"
    
    today = get_beijing_time().strftime("%Y-%m-%d")
    result = submit_step_data(userid, app_token, step, today)
    
    # 输出结果（隐藏部分账号信息）
    hidden_user = user[:3] + "****" + user[7:] if len(user) > 10 else user
    print(f"[{now}] {hidden_user} 步数同步成功: {step} 步 [{result}]")
    return f"同步成功: {step} 步"

def batch_process(users, passwords, min_step=800, max_step=1000):
    """批量处理多账号同步"""
    user_list = users.split('#')
    passwd_list = passwords.split('#')
    
    if len(user_list) != len(passwd_list):
        print("账号和密码数量不匹配！")
        return
    
    results = []
    for user, pwd in zip(user_list, passwd_list):
        results.append(main(user, pwd, min_step, max_step))
    return results

if __name__ == "__main__":
    # 从命令行参数获取账号密码（示例：python script.py user1#user2 pass1#pass2）
    if len(sys.argv) >= 3:
        users = sys.argv[1]
        passwords = sys.argv[2]
        batch_process(users, passwords, 2000, 2500)  # 实际使用建议设置合理步数范围
    else:
        print("使用方式: python script.py 账号#账号 密码#密码")