# -*- coding: utf8 -*-
from datetime import datetime, timedelta
import random
import sys
import requests
import re

# 全局请求头（更新User-Agent和版本）
headers = {
    'User-Agent': 'MiFit/6.3.5 (iPhone; iOS 16.0; Scale/3.00)',
    'Accept': 'application/json, text/plain, */*',
    'Content-Type': 'application/x-www-form-urlencoded'
}

def get_beijing_time():
    """获取北京时间（UTC+8），修复时区处理"""
    return datetime.now() + timedelta(hours=8)

def get_code(location):
    """从URL中提取认证码，增加异常处理"""
    try:
        code_pattern = re.compile("(?<=access=).*?(?=&)")
        return code_pattern.findall(location)[0]
    except:
        print("认证码提取失败，检查location格式")
        return None

def login(user, password):
    """登录小米运动账号（完整异常处理和参数优化）"""
    # 第一步：获取认证码
    url1 = f"https://api-user.huami.com/registrations/{user}/tokens"
    data1 = {
        "client_id": "HuaMi",
        "password": password,
        "redirect_uri": "https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html",
        "response_type": "token"
    }
    try:
        r1 = requests.post(url1, data=data1, headers=headers, allow_redirects=False, timeout=10)
        location = r1.headers.get("Location", "")
        if not location or "access=" not in location:
            print(f"登录第一步失败，状态码: {r1.status_code}，响应: {r1.text[:200]}")
            return None, None
        code = get_code(location)
        if not code:
            return None, None
    except Exception as e:
        print(f"登录第一步异常: {str(e)}")
        return None, None
    
    # 第二步：获取登录令牌和用户ID
    url2 = "https://account.huami.com/v2/client/login"
    data2 = {
        "app_name": "com.xiaomi.hm.health",
        "app_version": "6.3.5",
        "code": code,
        "country_code": "CN",
        "device_id": "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1",
        "device_model": "phone",
        "dn": "api-user.huami.com,api-mifit.huami.com,app-analytics.huami.com",
        "grant_type": "authorization_code",
        "lang": "zh_CN",
        "os_version": "16.0.0",
        "source": "com.xiaomi.hm.health",
        "third_name": "email"
    }
    try:
        r2 = requests.post(url2, data=data2, headers=headers, timeout=10)
        if r2.status_code != 200:
            print(f"登录第二步失败，状态码: {r2.status_code}，响应: {r2.text[:200]}")
            return None, None
        token_info = r2.json().get("token_info", {})
        return token_info.get("login_token"), token_info.get("user_id")
    except Exception as e:
        print(f"登录第二步异常: {str(e)}，响应: {r2.text[:200] if 'r2' in locals() else ''}")
        return None, None

def get_app_token(login_token):
    """获取应用令牌，增加空值校验"""
    if not login_token:
        return None
    url = f"https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com,api-mifit.huami.com,app-analytics.huami.com&login_token={login_token}"
    try:
        response = requests.get(url, headers=headers, timeout=5)
        if response.status_code == 200:
            return response.json().get("token_info", {}).get("app_token")
        print(f"获取app_token失败，状态码: {response.status_code}")
        return None
    except Exception as e:
        print(f"获取app_token异常: {str(e)}")
        return None

