def main(_user, _passwd, min_step=18000, max_step=22000):
    """主函数：登录并提交步数，增加日志输出"""
    user = str(_user).strip()
    password = str(_passwd).strip()
    step = random.randint(min_step, max_step)
    print(f"[任务开始] 账号: {user[:3]}****{user[-3:]}，步数: {step}")
    
    if not user or not password:
        print("[错误] 用户名或密码为空")
        return "login fail"
    
    login_token, userid = login(user, password)
    if not login_token or not userid:
        print("[错误] 登录失败，请检查账号密码")
        return "login fail"
    
    app_token = get_app_token(login_token)
    if not app_token:
        print("[错误] 获取应用令牌失败")
        return "token fail"
    
    today = get_beijing_time().strftime("%Y-%m-%d")
    result = submit_step_data(userid, app_token, step, today)
    
    print(f"[任务完成] {user[:3]}****{user[-3:]}，步数: {step}，结果: {result}")
    return f"success: {step} steps"

def batch_process(users, passwords, min_step=2000, max_step=2500):
    """批量处理多账号，增加进度提示"""
    user_list = users.split('#')
    passwd_list = passwords.split('#')
    
    if len(user_list) != len(passwd_list):
        print("[错误] 账号和密码数量不匹配")
        return
    
    print(f"[批量任务] 共 {len(user_list)} 个账号待处理")
    for i, (user, pwd) in enumerate(zip(user_list, passwd_list), 1):
        print(f"\n--- 处理账号 {i}/{len(user_list)} ---")
        main(user, pwd, min_step, max_step)
    print("\n[批量任务完成]")

if __name__ == "__main__":
    # 命令行参数示例: python script.py user1#user2 pass1#pass2
    if len(sys.argv) >= 3:
        users = sys.argv[1]
        passwords = sys.argv[2]
        batch_process(users, passwords)
    else:
        print("使用说明: python script.py 账号#账号 密码#密码")